import rock from "../image/rock.png";
import paper from "../image/paper.png";
import scissors from "../image/scissors.png";

function Hand({ selected }) {
  return (
    <div className=" mx-5 my-5">
      <img
        src={
          selected === "rock" ? rock : selected === "paper" ? paper : scissors
        }
        alt="Rock Paper Scissors"
      />
    </div>
  );
}

export default function ShowHands({ player, computer, count, status }) {
  return (
    <div className="show d-flex justify-content-center">
      <div className="player">
        {player !== "" && <Hand selected={player} />}
      </div>
      <div>
        <>Game</>
        <h1>{count}</h1>
        <h2>{status}</h2>
      </div>
      <div className="player">
        {computer !== "" && <Hand selected={computer} />}
      </div>
    </div>
  );
}
