import rock from "../image/rock.png";
import paper from "../image/paper.png";
import scissors from "../image/scissors.png";

export default function Button({ selectHand }) {
  return (
    <div>
      <button className="select mx-3" onClick={() => selectHand("rock")}>
        <img src={rock} alt="Rock Paper Scissors" />
      </button>
      <button className="select mx-3" onClick={() => selectHand("paper")}>
        <img src={paper} alt="Rock Paper Scissors" />
      </button>
      <button className="select mx-3" onClick={() => selectHand("scissors")}>
        <img src={scissors} alt="Rock Paper Scissors" />
      </button>
    </div>
  );
}
