import "./App.css";
import { useState } from "react";
import Button from "./component/Button";
import ShowHands from "./component/ShowHands";
import Score from "./component/Score";

function App() {
  const hands = ["rock", "paper", "scissors"];

  const [playerSelected, setPlayerSelected] = useState("");
  const [computerSelected, setComputerSelected] = useState("");

  const [playerScore, setPlayerScore] = useState(0);
  const [computerScore, setComputerScore] = useState(0);
  const [status, setStatus] = useState("");
  const [count, setCount] = useState(0);



  function selectHand(select) {
    const computerRandom = hands[Math.floor(Math.random() * 3)];

    setPlayerSelected(select);
    setComputerSelected(computerRandom);
    setCount(count + 1);

    if (select !== computerRandom) {
      if (select === "paper") {
        if (computerRandom === "rock") {
          setPlayerScore(playerScore + 1);
          setStatus("WIN");
        } else if (computerRandom === "scissors") {
          setComputerScore(computerScore + 1);
          setStatus("LOSE");
        }
      } else if (select === "rock") {
        if (computerRandom === "paper") {
          setComputerScore(computerScore + 1);
          setStatus("LOSE");
        } else if (computerRandom === "scissors") {
          setPlayerScore(playerScore + 1);
          setStatus("WIN");
        }
      } else {
        if (computerRandom === "paper") {
          setPlayerScore(playerScore + 1);
          setStatus("Win");
        } else if (computerRandom === "rock") {
          setComputerScore(computerScore + 1);
          setStatus("LOSE");
        }
      }
    } else {
      setStatus('DRAW')
    }
  }

  return (
    <div className="App">
      <header className="App-header">
        <h1>Paper Rock Scissors</h1>
      </header>
      <Score player={playerScore} computer={computerScore} />
      <ShowHands
        player={playerSelected}
        computer={computerSelected}
        count={count}
        status={status}
      />
      <Button selectHand={selectHand} />
    </div>
  );
}

export default App;
